// PopupForm.js

import React, { useState } from 'react';

function PopupForm({ isOpen, onClose, onSubmit, initialValues, setUsername, setPassword, setWebsite }) {
    const [newPasswordUsername, setNewPasswordUsername] = useState(initialValues.newPasswordUsername || ''); // Utilisation de useState avec les valeurs initiales
    const [newPasswordPassword, setNewPasswordPassword] = useState(initialValues.newPasswordPassword || ''); // Utilisation de useState avec les valeurs initiales
    const [newPasswordWebsite, setNewPasswordWebsite] = useState(initialValues.newPasswordWebsite || ''); // Utilisation de useState avec les valeurs initiales

    return (
        <>
            {isOpen &&
                <div className="popup-form">
                    <div className="popup-content">
                        <span className="close" onClick={onClose}>&times;</span>
                        <form onSubmit={onSubmit}>
                            <h2>Formulaire</h2>
                            <input type="text" placeholder="Nom d'utilisateur" value={newPasswordUsername} onChange={(e) => setUsername(e.target.value)} />
                            <input type="text" placeholder="Mot de passe" value={newPasswordPassword} onChange={(e) => setPassword(e.target.value)} />
                            <input type="text" placeholder="Site web" value={newPasswordWebsite} onChange={(e) => setWebsite(e.target.value)} />
                            <button type="submit">Soumettre</button>
                        </form>
                    </div>
                </div>
            }
        </>
    );
}

export default PopupForm;
