import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PopupForm from './PopupForm'; // Import du composant PopupForm
import PopupEditForm from "./PopupEditForm";

function PasswordListPage() {
    const [newPasswordUsername, setNewPasswordUsername] = useState('');
    const [newPasswordPassword, setNewPasswordPassword] = useState('');
    const [newPasswordWebsite, setNewPasswordWebsite] = useState('');
    const [generateStrongPassword, setGenerateStrongPassword] = useState(false);
    const [passwordLength, setPasswordLength] = useState(12);
    const [includeNumbers, setIncludeNumbers] = useState(false);
    const [includeSpecialChars, setIncludeSpecialChars] = useState(false);
    const [passwords, setPasswords] = useState([]);
    const [isFormOpen, setIsFormOpen] = useState(false); // Ajout de l'état pour gérer l'ouverture/fermeture du popup form
    const [selectedPassword, setSelectedPassword] = useState(null); // Ajout de l'état pour stocker le mot de passe sélectionné pour l'édition

    useEffect(() => {
        fetch('http://localhost:3001/passwords', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch passwords');
                }
                return response.json();
            })
            .then(data => {
                setPasswords(data);
            })
            .catch(error => {
                console.error('Error fetching passwords:', error);
            });
    }, []);

    const handleAddPassword = async () => {
        try {
            const response = await fetch('http://localhost:3001/passwords', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({
                    generateStrongPassword: generateStrongPassword,
                    passwordLength: passwordLength,
                    includeNumbers: includeNumbers,
                    includeSpecialChars: includeSpecialChars,
                    username: newPasswordUsername,
                    password: newPasswordPassword,
                    website: newPasswordWebsite
                }),
            });
            const data = await response.json();
            if (response.ok) {
                setPasswords([...passwords, data.newPassword]);
                setNewPasswordUsername('');
                setNewPasswordPassword('');
                setNewPasswordWebsite('');
                setGenerateStrongPassword(false);
                setPasswordLength(12);
                setIncludeNumbers(false);
                setIncludeSpecialChars(false);
                setIsFormOpen(false)
            } else {
                console.error('Erreur lors de l\'ajout du mot de passe :', data.error);
            }
        } catch (error) {
            console.error('Erreur lors de l\'ajout du mot de passe :', error);
        }
    };

    const handleEditPassword = async () => {
        if (!selectedPassword) return;

        const { id, username, password, website } = selectedPassword;

        try {
            const response = await fetch(`http://localhost:3001/passwords/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({
                    useStrongPassword: false,
                    username: username,
                    password: password,
                    website: website
                }),
            });
            if (response.ok) {
                const updatedPasswords = passwords.map(p => {
                    if (p.id === id) {
                        return {
                            ...p,
                            username: username,
                            password: password,
                            website: website
                        };
                    }
                    return p;
                });
                setPasswords(updatedPasswords);
                setIsFormOpen(false);
            } else {
                const data = await response.json();
                console.error('Erreur lors de la modification du mot de passe :', data.error);
            }
        } catch (error) {
            console.error('Erreur lors de la modification du mot de passe :', error);
        }
    };

    const handleDeletePassword = async (passwordId) => {
        try {
            const response = await fetch(`http://localhost:3001/passwords/${passwordId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
            });
            if (response.ok) {
                const updatedPasswords = passwords.filter(password => password.id !== passwordId);
                setPasswords(updatedPasswords);
            } else {
                const data = await response.json();
                console.error('Erreur lors de la suppression du mot de passe :', data.error);
            }
        } catch (error) {
            console.error('Erreur lors de la suppression du mot de passe :', error);
        }
    };

    return (
        <div>
            <h2>Liste des mots de passe</h2>
            {/* Bouton pour ouvrir le formulaire */}
            <button className="open-button" onClick={() => setIsFormOpen(true)}>Open Form</button>
            {/* Popup Form avec le formulaire pour ajouter un mot de passe */}
            <PopupForm
                isOpen={isFormOpen}
                onClose={() => setIsFormOpen(false)}
                onSubmit={handleAddPassword}
            >
                <div>
                    <h3>Ajouter un nouveau mot de passe</h3>
                    <input type="text" placeholder="Nom d'utilisateur" value={newPasswordUsername} onChange={(e) => setNewPasswordUsername(e.target.value)} />
                    <input type="text" placeholder="Mot de passe" value={newPasswordPassword} onChange={(e) => setNewPasswordPassword(e.target.value)} />
                    <input type="text" placeholder="Site web" value={newPasswordWebsite} onChange={(e) => setNewPasswordWebsite(e.target.value)} />
                    <label>
                        Générer un mot de passe fort :
                        <input type="checkbox" checked={generateStrongPassword} onChange={(e) => setGenerateStrongPassword(e.target.checked)} />
                    </label>
                    {generateStrongPassword && (
                        <div>
                            <label>
                                Longueur du mot de passe :
                                <input type="number" value={passwordLength} onChange={(e) => setPasswordLength(parseInt(e.target.value))} />
                            </label>
                            <label>
                                Inclure des chiffres :
                                <input type="checkbox" checked={includeNumbers} onChange={(e) => setIncludeNumbers(e.target.checked)} />
                            </label>
                            <label>
                                Inclure des caractères spéciaux :
                                <input type="checkbox" checked={includeSpecialChars} onChange={(e) => setIncludeSpecialChars(e.target.checked)} />
                            </label>
                        </div>
                    )}
                </div>
            </PopupForm>
            {/* Votre contenu existant */}
            <h3>Liste des mots de passe</h3>
            <ul>
                {passwords.map(password => (
                    <li key={password.id}>
                        <strong>{password.username}</strong>: {password.password} - <em>{password.website}</em>
                        <button onClick={() => handleDeletePassword(password.id)}>Supprimer</button>
                        <button className="open-button" onClick={() => setSelectedPassword(password)}>Edit</button>
                        <PopupEditForm
                            isOpen={selectedPassword !== null}
                            onClose={() => setSelectedPassword(null)}
                            onSubmit={handleEditPassword}
                        >
                            <div>
                                <h3>Modifier le mot de passe</h3>
                                <input type="text" placeholder="Nom d'utilisateur" value={selectedPassword.username} onChange={(e) => setSelectedPassword(prevState => ({ ...prevState, username: e.target.value }))} />
                                <input type="text" placeholder="Mot de passe" value={selectedPassword.password} onChange={(e) => setSelectedPassword(prevState => ({ ...prevState, password: e.target.value }))} />
                                <input type="text" placeholder="Site web" value={selectedPassword.website} onChange={(e) => setSelectedPassword(prevState => ({ ...prevState, website: e.target.value }))} />
                            </div>
                        </PopupEditForm>
                    </li>
                ))}
            </ul>
            <Link to="/dashboard">Retour au tableau de bord</Link>
        </div>
    );
}

export default PasswordListPage;
