import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PopupNotes from "./PopupNotes";
import PopupEditNotes from './PopupEditNotes';
function NotesPage() {
    const [newNoteTitle, setNewNoteTitle] = useState('');
    const [newNoteContent, setNewNoteContent] = useState('');
    const [notes, setNotes] = useState([]);
    const [isAddPopupOpen, setIsAddPopupOpen] = useState(false); // État local pour contrôler l'ouverture du popup d'ajout
    const [editNoteId, setEditNoteId] = useState(null); // État local pour stocker l'ID de la note en cours de modification
    const [editNoteTitle, setEditNoteTitle] = useState(''); // État local pour le titre de la note en cours de modification
    const [editNoteContent, setEditNoteContent] = useState(''); // État local pour le contenu de la note en cours de modification
    const [isEditPopupOpen, setIsEditPopupOpen] = useState(false); // État local pour contrôler l'ouverture du popup de modification


    useEffect(() => {
        // Effectuer une requête GET pour récupérer toutes les notes
        fetch('http://localhost:3001/notes', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch notes');
                }
                return response.json();
            })
            .then(data => {
                setNotes(data); // Mettre à jour les notes dans l'état local
            })
            .catch(error => {
                console.error('Error fetching notes:', error);
            });
    }, []);

    // Fonction pour ajouter une nouvelle note
    const handleAddNote = async () => {
        try {
            const response = await fetch('http://localhost:3001/notes', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({
                    Title: newNoteTitle,
                    Content: newNoteContent
                }),
            });
            const data = await response.json();
            if (response.ok) {
                // Si la note est ajoutée avec succès, mettre à jour la liste des notes
                setNotes([...notes, data.note]);
                // Effacer les champs de saisie pour une nouvelle note
                setNewNoteTitle('');
                setNewNoteContent('');
            } else {
                console.error('Erreur lors de l\'ajout de la note :', data.error);
            }
        } catch (error) {
            console.error('Erreur lors de l\'ajout de la note :', error);
        }
    };

    // Fonction pour supprimer une note
    const handleDeleteNote = async (noteId) => {
        try {
            const response = await fetch(`http://localhost:3001/notes/${noteId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
            });
            if (response.ok) {
                // Si la note est supprimée avec succès, mettre à jour la liste des notes en retirant la note supprimée
                const updatedNotes = notes.filter(note => note.NoteID !== noteId);
                setNotes(updatedNotes);
            } else {
                const data = await response.json();
                console.error('Erreur lors de la suppression de la note :', data.error);
            }
        } catch (error) {
            console.error('Erreur lors de la suppression de la note :', error);
        }
    };

    // Fonction pour modifier une note
    const handleEditNote = async () => {
        try {
            const response = await fetch(`http://localhost:3001/notes/${editNoteId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({
                    Title: editNoteTitle,
                    Content: editNoteContent
                }),
            });
            const data = await response.json();
            if (response.ok) {
                // Si la note est modifiée avec succès, mettre à jour la liste des notes
                const updatedNotes = notes.map(note => {
                    if (note.NoteID === editNoteId) {
                        return {
                            ...note,
                            Title: editNoteTitle,
                            Content: editNoteContent
                        };
                    }
                    return note;
                });
                setNotes(updatedNotes);
                setIsPopupOpen(false); // Fermer le popup après la modification
            } else {
                console.error('Erreur lors de la modification de la note :', data.error);
            }
        } catch (error) {
            console.error('Erreur lors de la modification de la note :', error);
        }
    };
    const openEditPopup = (noteId, title, content) => {
        setEditNoteId(noteId);
        setEditNoteTitle(title);
        setEditNoteContent(content);
        setIsEditPopupOpen(true);
    };
    const openAddPopup = () => {
        setIsAddPopupOpen(true);
    };

    // Fonction pour fermer le popup
    const closePopups = () => {
        setIsAddPopupOpen(false);
        setIsEditPopupOpen(false);
    };
    return (
        <div>
            <h2>Notes</h2>
            <div>
                <h3>Ajouter une nouvelle note</h3>
                <button onClick={openAddPopup}>Ajouter une note</button> {/* Bouton pour ouvrir le popup d'ajout */}
                <PopupNotes isOpen={isAddPopupOpen} onClose={closePopups} onSubmit={handleAddNote}>
                    {/* Contenu du formulaire */}
                    <input type="text" placeholder="Titre de la note" value={newNoteTitle} onChange={(e) => setNewNoteTitle(e.target.value)} />
                    <textarea placeholder="Contenu de la note" value={newNoteContent} onChange={(e) => setNewNoteContent(e.target.value)} />
                </PopupNotes>
            </div>
            <h3>Liste des notes</h3>
            <ul>
                {notes.map(note => (
                    <li key={note.NoteID}>
                        <strong>{note.Title}</strong>: {note.Content}
                        <button onClick={() => openEditPopup(note.NoteID, note.Title, note.Content)}>Modifier</button>
                        <PopupEditNotes isOpen={isEditPopupOpen && editNoteId === note.NoteID} onClose={closePopups} onSubmit={handleEditNote}>
                            {/* Contenu du formulaire */}
                            <input type="text" placeholder="Titre de la note" value={editNoteTitle} onChange={(e) => setEditNoteTitle(e.target.value)} />
                            <textarea placeholder="Contenu de la note" value={editNoteContent} onChange={(e) => setEditNoteContent(e.target.value)} />
                        </PopupEditNotes>
                        <button onClick={() => handleDeleteNote(note.NoteID)}>Supprimer</button>
                    </li>
                ))}
            </ul>
            <Link to="/dashboard">Retour au tableau de bord</Link>
        </div>
    );
}


export default NotesPage;
