import React from 'react';
import { useHistory } from 'react-router-dom';

function DashboardPage() {
    // Vérifier si l'utilisateur est authentifié
    const isAuthenticated = localStorage.getItem('token') !== null;
    const history = useHistory();

    // Fonction pour rediriger vers la page Notes
    const goToNotesPage = () => {
        history.push('/notes');
    };

    // Fonction pour rediriger vers la page Password List
    const goToPasswordListPage = () => {
        history.push('/password-list');
    };

    return (
        <div>
            <h2>Dashboard</h2>
            {isAuthenticated && (
                <div>
                    <h3>Menu</h3>
                    <button onClick={goToNotesPage}>Notes</button>
                    <button onClick={goToPasswordListPage}>Password List</button>
                </div>
            )}
        </div>
    );
}

export default DashboardPage;
