import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function PasswordListPage() {
    const [showForm, setShowForm] = useState(false);
    const [newPasswordUsername, setNewPasswordUsername] = useState('');
    const [newPasswordPassword, setNewPasswordPassword] = useState('');
    const [newPasswordWebsite, setNewPasswordWebsite] = useState('');

    const handleAddPassword = async () => {
        // Envoyer la requête pour ajouter le mot de passe
        setShowForm(false); // Fermer le formulaire après l'ajout
    };

    return (
        <div>
            <h2>Liste des mots de passe</h2>
            <button className="open-button" onClick={() => setShowForm(true)}>Ajouter un mot de passe</button>
            {showForm && (
                <div className="form-popup">
                    <form className="form-container">
                        <h1>Ajouter un mot de passe</h1>
                        <label htmlFor="username"><b>Nom d'utilisateur</b></label>
                        <input type="text" placeholder="Nom d'utilisateur" value={newPasswordUsername} onChange={(e) => setNewPasswordUsername(e.target.value)} required />

                        <label htmlFor="password"><b>Mot de passe</b></label>
                        <input type="password" placeholder="Mot de passe" value={newPasswordPassword} onChange={(e) => setNewPasswordPassword(e.target.value)} required />

                        <label htmlFor="website"><b>Site web</b></label>
                        <input type="text" placeholder="Site web" value={newPasswordWebsite} onChange={(e) => setNewPasswordWebsite(e.target.value)} required />

                        <button type="button" className="btn" onClick={handleAddPassword}>Ajouter le mot de passe</button>
                        <button type="button" className="btn cancel" onClick={() => setShowForm(false)}>Fermer</button>
                    </form>
                </div>
            )}
            <h3>Liste des mots de passe</h3>
            {/* Afficher la liste des mots de passe */}
            <ul>
                {/* Boucle sur les mots de passe et affiche chaque élément */}
            </ul>
            <Link to="/dashboard">Retour au tableau de bord</Link>
        </div>
    );
}

export default PasswordListPage;
